    static NanoSecondsChrono chrono = new NanoSecondsChrono();

    public static void main(String[] args){

        Sort[] sorts = {
                new BubbleSort(myList),
                new InsertionSort(myList),
                new QuickSort(myList)
            };

        for(int i = 0; i<sorts.length; i++){
            Sort sort = sorts[i];

            chrono.start();
            sort.sortIntegers();
            chrono.stop();
            System.out.println(getDurationString(sort.getName(), chrono.getInterval()));

            chrono.reset();
        }
    }

    private static String getDurationString(String name, long interval) {
        if(interval == -1){
            return  null;
        }
        DecimalFormat decimalFormat = new DecimalFormat();
        decimalFormat.setGroupingUsed(true);
        decimalFormat.setGroupingSize(3);

        return name + " sorting took " + decimalFormat.format(interval) + " nanoseconds";
    }
